# Get Power BI Desktop AS Port (may need elevated permissions)
Get-NetTCPConnection | ? OwningProcess -in (gwmi Win32_Process -f "CommandLine LIKE '%Desktop%' and Name ='msmdsrv.exe'").ProcessId|? LocalAddress -eq '127.0.0.1'|select Local*,OwningProcess,@{n="Desktop Title";e={(gps -Id (gcim Win32_Process -f "ProcessId = $($_.OwningProcess)").ParentProcessId).MainWindowTitle}},@{n="Path";e={(gps -Id $_.OwningProcess).Path}}|ft
